INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100001, NULL, 'Красноярская краевая филармония', 'Концерт лауреатов Всесоюзного конкурса им. Глинки', 'Россия',
        'Красноярск', NULL, 'площадь Мира, 2Б, Красноярск, Россия',
        'В октябре 1987 года Дмитрию Хворостовскому присуждена 1-я премия Всесоюзного конкурса певцов имени М.И. Глинки. Вместе с другими лауреатами он отправляется в турне. В этом же году в Красноярском государственном театре оперы и балета ставят «Фауста» Шарля Гуно, где Хворостовский исполняет партию Валентина. Через три года Дмитрий даст прощальный бенефис в своем родном городе и начнет сольную карьеру на Западе. Такому активному карьерному взлету во многом поспособствовала заметившая артиста Ирина Архипова, легенда советской оперной сцены, которая не только провезла лауреатов конкурса по их родным городам, но и представила их на концертных площадках США.',
        '10.06.1987');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100002, NULL, 'Красноярская краевая филармония', 'Концерт памяти Евгения Лозинского', 'Россия', 'Красноярск',
        NULL, 'пр. Мира, 2Б, Красноярск, Красноярский край, 660049',
        '9 июля 1997 года в возрасте 62 лет умирает Евгений Лозинский — выдающийся педагог, друг и наставник Хворостовского. 10 июля в Малом зале филармонии Дмитрий в сопровождении Михаила Аркадьева дает первый концерт из цикла, посвященного памяти своего учителя. Частью курса Лозинского «Основы музыкальной культуры» были вечера классической музыки именно в Малом зале. Студенты, включая юного Хворостовского, посещали вечера бесплатно, приобщаясь к высокому искусству.',
        '10.07.1997');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100003, NULL, 'Красноярский государственный театр оперы и балета', 'Выступление на бенефисе Ларисы Марзоевой',
        'Россия', 'Красноярск', NULL, 'ул. Перенсона, 2, Красноярск, Красноярский край, 660049',
        '«Проблема 2000 года» не стала концом света, как обещали в конце 90-х мировые СМИ, и 14 января в Красноярском театре оперы и балета проходит бенефис сопрано Ларисы Марзоевой. Дата концерта специально выбрана так, чтобы на нем смог выступить ее друг и партнер по сцене Дмитрий Хворостовский, приехавший на благотворительный концерт в Красноярскую краевую филармонию. Вместе они исполняют дуэт Джильды и Риголетто из оперы Верди «Риголетто».',
        '14.01.2000');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100004, NULL, 'Красноярский государственный институт искусств', 'Поступление на вокальный факультет', 'Россия',
        'Красноярск', NULL, 'ул. Ленина, 22, Красноярск, Красноярский край, 660020',
        'Приемные экзамены в Красноярский государственный институт искусств — настоящее испытание для Хворостовского. Он хочет петь арию Ксеркса на итальянском, но разрешили только на русском. Это тяжело дается 19-летнему абитуриенту, но уже к третьей песне — народной «Ах ты, душечка» — Дмитрий «разгоняется» и показывает, на что способен. В свой класс его берет Екатерина Иофель, о сложном характере которой ходили легенды. Но именно она «раскрыла» его не как тенора, а как выдающегося баритона.',
        '04.06.1905');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100005, NULL, 'Красноярское педагогическое училище №1 им. М. Горького', 'Поступление в училище', 'Россия',
        'Красноярск', NULL, 'ул. Урицкого, 106, Красноярск, Красноярский край, 660017',
        'В 1979 году Хворостовского принимают в педучилище. На вступительных он предстает не по годам серьезным: поет тоскливый романс “Утро туманное”, чем изрядно веселит комиссию.  От юношеского бунтарства спасают уроки с Галиной Астаниной - внимательным педагогом, чутким к его таланту. Она убеждает Дмитрия поверить в свою уникальность, учит серьезно относиться к делу: «никому не давай трогать свой голос, не увлекайся диапазоном, занимайся дыханием, кантиленой, пением».',
        '01.06.1905');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100006, NULL, 'Красноярский государственный театр оперы и балета', 'Первая оперная партия', 'Россия',
        'Красноярск', NULL, 'ул. Перенсона, 2, Красноярск, Красноярский край, 660049',
        'В середине декабря 1984 года температура в Красноярске упала до -39 градусов, но к Новому году «потеплело» до  -14°C. 30 декабря зрители в театре оперы и балета, сдав шубы в гардероб, ждут начала оперы «Риголетто». Они еще не знают, что совсем скоро станут свидетелями первой оперной партии 22-летнего студента 2 курса Дмитрия Хворостовского и не придают этому никакого значения. В тот вечер он выйдет в небольшой роли Марулло, но очень быстро дорастет до ведущей партии трагического шута Риголетто.',
        '30.12.1984');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100007, NULL, 'Красноярский государственный театр оперы и балета', 'Спектакль «Терем-Теремок» (роль - Ёжик)',
        'Россия', 'Красноярск', NULL, 'ул. Перенсона, 2, Красноярск, Красноярский край, 660050',
        'Хворостовскому 22, он играет Ёжика в детской сказке «Терем-Теремок». Всё согласно правилам театра - для детей работают только артисты первого эшелона. Партнерша Дмитрия вспоминает: «Быстро стало понятно, что работать с ним - одно удовольствие». Маленькие зрители приходят в полный восторг.  Конечно, тогда никто не может предположить, что спустя 33 года театру присвоят имя Хворостовского.',
        '02.06.1985');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100008, NULL, 'Красноярский государственный театр оперы и балета', 'Дебют в партии Онегина', 'Россия',
        'Красноярск', NULL, 'ул. Перенсона, 2, Красноярск, Красноярский край, 660049',
        'Яркое выступление Хворостовского в Красноярском театре оперы и балета. Звучит одна из первых его крупных партий: Онегин в опере Чайковского. Позже он исполнит ее на сценах Венеции, Парижа и Лондона. Солистка Лариса Марзоева, игравшая Татьяну, навсегда запомнит Дмитрия как энергичного и вдохновенного партнера. Однажды во время репетиции она увидит у певца слезы на глазах. На вопрос о том, что случилось, он ответит: «Слушай, невероятно - как, оказывается, можно спеть эту сцену!»',
        '07.06.1905');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100009, NULL, 'Красноярский государственный театр оперы и балета ', 'Дебют в «Пиковой даме»', 'Россия',
        'Красноярск', NULL, 'ул. Перенсона, 2, Красноярск, Красноярский край, 660049',
        'Ноябрь 1985 года — Microsoft выпускает первый Windows, а Гарри Каспаров становится самым молодым чемпионом мира по шахматам. На сцене Красноярского театра оперы и балеты студент 3-го курса Дмитрий Хворостовский дебютирует в роли князя Елецкого в «Пиковой даме» Петра Чайковского. До нее он пел партии второго плана, но всегда относился к этому с пониманием: «Должна же быть какая-то субординация, я же был там самым молодым!»',
        '12.11.1985');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100010, NULL, 'Красноярский государственный театр оперы и балета ', 'Опера «Фауст»', 'Россия', 'Красноярск',
        NULL, 'ул. Перенсона, 2, Красноярск, Красноярский край, 660049',
        'В октябре 1996 года в Красноярске открывается фестиваль «Мастера оперы и балета России». Хворостовский дебютирует в партии Валентина в опере Шарля Гуно «Фауст». Роль небольшая, но выразительная. В свое время «Фауст» был самой популярной оперой мира, а в Ковент-Гарден она шла без перерыва с 1863 по 1911 год. В 1994 году Дмитрий уже выходил с этой партией на сцену Метрополитен-опера, а сегодня — на малой Родине.',
        '23.10.1996');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100011, NULL, 'Красноярская краевая филармония', 'Благотворительный концерт', 'Россия', 'Красноярск', NULL,
        'площадь Мира, 2Б, Красноярск, Красноярский край, 660049',
        '7 декабря 1988 года в Армении происходит мощнейшее землетрясение — десятки тысяч погибших, полмиллиона человек остались без жилья. Свою помощь в восстановлении разрушенных городов оказывают 111 стран. 21 января 1989 года Дмитрий Хворостовский вместе с Ларисой Марзоевой выступают на благотворительном концерте с ариями из опер Вагнера, Верди и Чайковского. Собранные средства перечислены в Фонд пострадавших от землетрясения в Армении.',
        '21.01.1989');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100012, NULL, 'Красноярский государственный театр оперы и балета ', 'Дебют в партии Фигаро', 'Россия',
        'Красноярск', NULL, 'ул. Перенсона, 2, Красноярск, Красноярский край, 660049',
        'В январе 1990 года Горбачев возвращает Мстиславу Ростроповичу гражданство СССР (тот его не принимает). А Хворостовский дебютирует в партии Фигаро в опере «Севильский цирюльник» Россини. В 19 веке эту партию пел бас, по новой классификации — баритон. Фигаро пел голосом Юрия Гуляева, Александра Инашвили и Муслима Магомаева. Каватину Фигаро Дмитрий будет исполнять и в сентябре того же года, но уже в Москве, на своем сольном концерте на сцене Большого театра.',
        '1990,январь');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100013, NULL, 'Красноярский государственный театр оперы и балета ', 'Прощальный бенефис', 'Россия',
        'Красноярск', NULL, 'ул. Перенсона, 2, Красноярск, Красноярский край, 660049',
        'Хворостовский дает прощальный бенефис на родной красноярской сцене. Он окрылён успехом: после Кардиффа предложения от легендарных театров мира не иссякают. Так что контракт в Красноярске решено не продлевать. Всю свою богатую фонотеку Дмитрий отдает другу-тенору, и переезжает в свой новый дом - в Москву, где уже дали квартиру.  Ему 28, и его головокружительная карьера только начинается.',
        '10.12.1990');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100014, NULL, 'Дворец спорта имени Ивана Ярыгина', 'Песни военных лет', 'Россия', 'Красноярск', NULL,
        'ул. Остров Отдыха, 12, Красноярск, Красноярский край, 660064',
        '«В детстве я хорошо и много играл в футбол – мама начинала сомневаться в моих умственных способностях, когда я вместо рояля и уроков днями стучал мячом в стенку» - рассказывал Дмитрий Хворостовский. И на крупном стадионе он позже выступит, но в другом амплуа. В 2015 году оперный певец прилетел в родной город и впервые выступил во Дворце спорта им. Ивана Ярыгина – одном из крупнейших спортивных стадионов в Красноярске.',
        '27.05.2015');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100015, NULL, 'Красноярский государственный театр оперы и балета ', 'Юбилей Красноярского края', 'Россия',
        'Красноярск', NULL, 'ул. Перенсона, 2, Красноярск, Красноярский край, 660049',
        'Концерт, посвящённый 75-летию Красноярского края, с Академическим большим хором Российского государственного музыкального центра «Мастера хорового пения» Российского государственного музыкального телерадиоцентра под управлением Льва Конторовича',
        '10.12.1990');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100016, NULL, 'Отель Дворца Эмирейтс', 'Дебют в Арабских Эмиратах', 'ОАЭ', 'Абу-Даби', NULL,
        'Palace - Al Sufouh Rd - Al Ras Al Akhdar - Dubai - ОАЭ',
        'Хворостовский выступает в Арабских Эмиратах в рамках Восьмого международого оперного фестиваля. Концерт «Дмитрий Хворостовский и друзья» с участием Екатерины Сюриной, с Русским Национальным оркестром, дирижирует итальянец Никола Луизотти. Павильон спроектирован Захой Хадид. Концерт собирает полный зал, в числе зрителей - члены королевской семьи. Фестиваль под патронажем Шейха Мохаммеда бин Заеда Аль-Нахьяна, наследного принца Абу-Даби. До выступления Хворостовский беспокоится: чего ожидать от незнакомой публики? И напрасно! Зал "на ура" принимает и Верди, и Моцарта, и Россини.',
        '04.04.2011');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100017, NULL, 'Фестивальный дворец', 'Дебют в опере Верди “Разбойники”', 'Германия', 'Баден-Баден', NULL,
        'Beim Alten Bahnhof 2, 76530 Baden-Baden, Germany',
        '25-летний Хворостовский дебютирует в опере Верди “Разбойники”, исполняет роль Франческо. Это премьерная постановка Королевского оперного театра Ковент-Гарден, но играть ее приходится не в Лондоне, а в немецком Баден-Бадене - сцена Ковент-Гардена на реконструкции. Критики отмечают не только элегантность режиссуры, но и мастерство Хворостовского — его злодей Франческо приковывает взгляды даже весьма искушенной публики.',
        '1998, июнь (8/ 11/ 13)');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100018, NULL, ' Азербайджанская государственная академическая филармония имени Муслима Магомаева',
        'ХII Всесоюзный конкурс вокалистов им. М. Глинки', 'Азербайджан', 'Баку', NULL,
        'ул. Суверенитета, 2, Баку, Азербайджан',
        'Хворостовский завоевывает первое место на XII Всесоюзном конкурсе вокалистов имени Глинки. «На конкурсе бурлили подводные течения, различные кланы двигали вперед собственных фаворитов, и лишь я не знал в жюри ни единого человека», — расскажет Хворостовский в одном из интервью. Председатель жюри Ирина Архипова аплодирует стоя. Покорён и Муслим Магомаев: «У него дыхания на полстраницы». Хворостовский получает также Приз за лучшее исполнение арии из оперы Чайковского. Посыпались предложения от лучших театров СССР, разрешено давать сольные концерты в столичных городах. Но Хворостовский ещё не готов покидать родной Красноярск.  В те же дни свой триумф празднует Иосиф Бродский: ему присудили Нобелевскую премию по литературе.',
        '05-28.10.1987');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100019, NULL, 'Бостонская опера', 'Тэнглвудский фестиваль', 'США', 'Бостон', NULL,
        '301 Massachusetts Ave, Boston, MA 02115, United States',
        'В октябре 1991 года Хворостовский находится в США. На днях губернатор Арканзаса Билл Клинтон выдвинул свою кандидатуру на пост президента страны. Публика в Бостонском Симфоническом зале обсуждает, есть ли у него шансы. Но когда гаснет свет, разговоры утихают. Под руководством японского дирижера Сэйдзи Одзава Хворостовский исполняет партию Елецкого в «Пиковой даме» Петра Чайковского.',
        '1991, октябрь');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100020, NULL, 'Площадь Евровидения', 'VII open-air фестиваля Viva Musica! ', 'Словакия', 'Братислава ', NULL,
        '811 02 Bratislava, Slovakia',
        'Фестиваль Viva Musica! проходит в столице Словакии с 2004 года. Хворостовский приезжает на него в июле 2011 года и участвует в рамках VII open-air (свежий воздух) с оркестром Словакии, дирижёр Константин Орбелян. Маэстро надеется, что завтра во время выступления погода не подведет и шутит, что когда он выступает под открытым небом всегда идет дождь. Певец не ошибся — весь день моросит дождь, однако он не мешает зрителям наслаждаться произведениями в исполнении Дмитрия.',
        '30.07.2011');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100021, NULL, 'Королевский оперный театр «Ла Монне/Де Мюнт» ',
        'Дебют в опере Гаэтано Доницетти «Фаворитка» в партии короля Альфонсо XI', 'Бельгия', 'Брюссель ', NULL,
        '(La Monnaie/De Munt). Pl. de la Monnaie, 1000 Bruxelles',
        'В ноябре 1991 года Дмитрий Хворостовский приезжает в Брюссель, чтобы выступить в «колыбели» Бельгийской революции — Королевском оперном театре «Ла Монне/Де Мюнт». Он дебютирует в опере Гаэтано Доницетти «Фаворитка» в партии короля Альфонсо XI.',
        '1991, ноябрь');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100022, NULL, 'Театр Ронахер', 'Награда «Золотой Папагено»', 'Австрия', 'Вена', NULL,
        'Seilerstätte 9, 1010 Wien, Австрия',
        '27 июня 2016 года в театре Ронахер в Вене Дмитрий Хворостовский получает награду «Золотой Папагено», которая присуждается Австрийской музыкальной театральной премией. "Ты – потрясающий певец, певец международного масштаба, удивительный человек," сказал, обращаясь к российскому певцу, один из основателей премии, австрийский баритон Даниэль Серафин.',
        '27.06.2016');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100023, NULL, 'Венская государственная опера', 'Дебют в легендарном театре Австрии', 'Австрия', 'Вена ', NULL,
        'Opernring 2, 1010 Wien, Австрия',
        'В 1994 году Хворостовский дебютирует в легендарном австрийском театре в опере Винченцо Беллини «Пуритане». В течение следующих 22 лет он выступит на этой сцене ещё 73 раза, и Венская опера займет третье место по частоте его выступлений. В 2016 году Хворостовский здесь выступит в последний раз — в опере Джузеппе Верди «Травиата». В день кончины певца 22 ноября 2017 годы на здании Венской оперы вывесят черный траурный флаг.',
        '1994, май');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100024, NULL, 'Театр ла Фениче', 'Партия Онегина в опере Чайковского', 'Италия', 'Венеция', NULL,
        'Campo S. Fantin, 1965, 30124 Venezia VE, Италия',
        'Советский молодой баритон из Сибири — главная сенсация в мире оперы, после победы в 1989 году в Международном конкурсе певцов в Кардиффе он получил приглашения из лучших оперных театров мира — от лондонского Ковент-Гардена и Баварской оперы до Миланского Ла Скала и Нью-Йоркской Метрополитен Опера. В феврале 1991 года в Венеции Хворостовский испоняет партию Онегина в опере «Евгений Онегин» Чайковского. Примерно в это же время по улицам этого же города прогуливается поэт Иосиф Бродский.',
        '1991, февраль');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100025, NULL, 'Имперский Кургаус', '«Арт Ассамблеи. Дни российской культуры»', 'Германия', 'Вис-Баден', NULL,
        '65189 Wiesbaden, Германия',
        'В феврале 2014 года в рамках проекта "Арт Ассамблеи. Дни российской культуры" Хворостовский собирает аншлаг в роскошном Имперском Курхаусе немецкого города Висбаден. В программе вечера — сочинения Чайковского, Рахманинова, Мусоргского, Листа. За роялем — эстонский пианист Ивари Илия. По сообщению организаторов, публика долго не отпускала солиста со сцены и устроила продолжительные овации.',
        '03.02.2014');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100026, NULL, 'Замок Графенег', 'Гала-концерт «Праздник летнего солнцестояния»', 'Австрия', 'Графенег', NULL,
        'Grafenegg 10, 3485 Grafenegg, Австрия',
        'Последний концерт Хворостовского проходит в Австрии, на сцене старинного замка. Он поет арии из опер Чайковского, Моцарта, Верди. Исполняет сложнейший репертуар точно и проникновенно, несмотря на болезнь и июньскую жару. Вместе с ним выступает молодая российская певица Аида Гарифуллина.',
        '22.06.2017');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100027, NULL, 'Национальный театр в Мексике', 'Выступление в Мексике', 'Мексика', 'Мехико', NULL,
        'De Sopena 10, Centro, 36000 Guanajuato, Gto., Мексика',
        'В марте 1998 года Хворостовский готовится к своему выступлению на сцене Национального театра в Мексике, когда внезапно узнает о том, что его партнерша — сопрано Априле Милло — отменила свое выступление по личным причинам (заболела ее мать). Ей срочно находят замену в лице опытной американки Джун Андерсон, выступавшей с Паваротти. В итоге концерт проходит превосходно.  Ранее в тексте история с Априле Милло относилась к выступлению на фестивале "Сервантино", сейчас эта же история на выступлении в Национальном театре!  Пожалуйста!!! Напишите корректно текст!!!',
        '26.03.1998');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100028, NULL, 'Театр Фриско', 'Дебют в партии Андрея Болконсокго в опере "Война и мир"', 'США', 'Даллас', NULL,
        ' 8004 Dallas Pkwy Suite 200, Frisco, TX 75034, Соединенные Штаты',
        'В 1991 году в театре Фриско в пригороде Далласа Дмтирий Хворотсовский дебютирует в партии Андрея Болконского в опере Сергея Прокофьева «Война и мир». Через 10 лет в 2002 году Хвороствоский исполнил партию Болконсокго в Метрополитен-опера в Нью-Йорке (постановка А. Кочаловского).',
        '1991');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100029, NULL, 'Концертный зал Донецкой областной филармонии им. С.С. Прокофьева', 'Сольный концерт в Донецке',
        NULL, 'Донецк', NULL, 'ул. Постышева, д.117, Донецк',
        'В ноябре Дмитрий Хворостовский выступает с сольной программой в Донецке. В начале того же месяца в ГДР начинается снос Берлинской стены, а в Чехословакии - «Бархатная революция». Распад Советского Союза набирает обороты.',
        '27.11.1989');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100030, NULL, 'Тонхалле', '«Арт Ассамблеи. Дни российской культуры»', 'Германия', 'Дюссельдорф', NULL,
        'Ehrenhof 1, 40479 Düsseldorf, Германия',
        'Хворостовский концертирует в Германии с эстонским пианистом Ивари Илией. Концерты в рамках проекта «Арт Ассамблеи. Дни российской культуры», в преддверии международного культурно-делового форума «Дни России в Европе». В программе русская и немецкая музыка: Мусоргский, Лист, Чайковский, Рахманинов. Первое место выступления - курортные Висбаден и Баден-Баден, затем концертный зал Дюссельдорфа с величественным куполом и Берлин.',
        '09.02.2014');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100031, NULL, 'Свердловский государственный академический театр оперы и балета',
        'XX Всероссийский смотр вокалистов', 'Россия', 'Свердловск', NULL, 'просп. Ленина, 46А, ',
        'Хворостовский участвует в XX Всероссийском смотре вокалистов. Это конкурс выпускников музыкальных вузов, куда его везет педагог по вокалу Екатерина Иофель. На заключительном концерте Хворостовский исполняет арию Елецкого из оперы Петра Ильича Чайковского «Пиковая дама». Хворостовского замечают и приглашают участвовать в спектакле Кировского театра    Той же весной в СССР с официальным визитом приезжает Маргарет Тэтчер, премьер-министр Великобритании. И остается довольна: «Это был самый великолепный и мобилизующий из всех зарубежных визитов',
        '17.04.1987');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100032, NULL, 'Свердловская государственная академическая филармония',
        'Концерт в рамках проекта "Хворостовский и друзья"', 'Россия', 'Екатеринбург ', NULL,
        'ул. Карла Либкнехта, 38А, Екатеринбург, Свердловская обл., Россия, 620075',
        'Очередной концерт цикла “Хворостовский и друзья” проходит в новом формате. Вместо привычного дуэта - трио. С Дмитрием поёт Екатерина Сюрина, а также муж Сюриной, американский тенор Чарльз Кастроново. Программа продумана в живом развлекательном ключе: система микрофонов устроена так, чтобы артисты могли “свободно двигаться”. Первоклассный вокал, остроумные сценки. В итоге - значительное событие для музыкального Екатеринбурга',
        '19.12.2010');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100033, NULL, 'Большой театр Женевы', 'Опера Вольфганга Амадея Моцарта  "Дон Жуан", партия Дон Жуана',
        'Швейцария', 'Женева', NULL, 'Bd du Théâtre 11, 1204 Genève, Швейцария',
        'В 1999 году Хворостовский приезжает в Швейцарию, чтобы исполнить партию Дон Жуана в опере Вольфганга Амадея Моцарта "Дон Жуан". Здесь Дмитрий познакомился с Флоранс Илли, которая исполняет роль Церлины. Между ними пробегает искра.',
        '1999');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100034, NULL, 'Малый фестивальный театр', 'Зальцбургский фестиваль', 'Австрия', 'Зальцбург', NULL,
        'Hofstallgasse 1, 5020 Salzburg, Австрия',
        'Два года подряд — в 1995 и 1996-ом — Хворостовский приезжает в Австрию на Зальцбургский фестиваль. Основатели мероприятия особо подчеркивали важность музыки австро-немецких композиторов, поэтому Дмитрий именно здесь впервые исполняет партию графа Альмавивы в опере Вольфганга Амадея Моцарта "Женитьба Фигаро». В августе 2004 года он вернется на фестиваль, но уже с оперой «Война и мир» Сергея Прокофьева и партией Болконского в сопровождении Мариинского театра под руководством дирижера Валерия Гергиева.    Спектакль для истории записывает местное телевидение. Видео: https://www.youtube.com/watch?v=0vbd0TLY7Sc&t=173s&ab_channel=Bethanythorn',
        '1995, 1996, 2004');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100035, NULL, 'Большой фестивальный зал', 'Зальцбургский фестиваль, партия Дон Жуана', 'Австрия', 'Зальцбург',
        NULL, 'Большой фестивальный театр. Schwarzstraße 22, 5020 Salzburg, Австрия',
        'Дмитрий Хворостовский поет партию Дон Жуана в одноименной опере Вольфганга Амадея Моцарта в программе Зальцбургского фестиваля. «Каждый уважающий себя мужчина в душе Дон Жуан», – говорит он. Для Дмитрия Дон Жуан не просто великий любовник, а самый настоящий герой, вызывающий зависть и умирающий из-за этого. Последний раз Хворостовский исполнит партию Дон Жуана в январе 2004 года в Государственном оперном театре Гамбурга (Германия).',
        '1998, 1999, 2016');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100036, NULL, 'Зеленогорский городской дворец культуры', 'Заглавная партия в опере «Евгений Онегин»', 'Россия',
        'Зеленогорск', NULL, 'ул. Бортникова, 1, Зеленогорск, Россия',
        'На гастролях Красноярского государственного театра оперы и балета в Зеленогорске Хворостовский поет заглавную партию в опере «Евгений Онегин» Петра Ильича Чайковского. В то время «город атомщиков» называется Красноярск-45 и закрыт для посещения без особого приглашения.',
        '12.04.1988');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100037, NULL, '“Театрон Иерусалим”', 'Выступление на “Фестивале Израиля”', 'Израиль', 'Иерусалим ', NULL,
        'David Marcus St 20, Jerusalem, Израиль',
        'Хворостовский впервые выступает в Иерусалиме, на “Фестивале Израиля”. С ним давний партнер по сцене - пианист Михаил Аркадьев, о котором Хворостовский говорит: “У нас абсолютно идентичные взгляды на искусство и абсолютно, на мой взгляд, идеальный ансамбль". Для многих израильтян сочиения Малера и Свиридова в исполнении дуэта становятся одним из сильнейших музыкальных впечатлений в жизни.  Через пару дней Хворостовский участвует в концерте в рамках международного фестиваля с Санкт-Петербургским камерным хором Н.Н. Корнева с программой духовной музыки. И третье выступление в Израиле было в июле 2009 - концерт с Раананским симфоническим оркестром (дирижёр Ион Марин) в Амфитеатре Раананы в Тель-Авиве.',
        '1997');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100038, NULL, 'Зал Кенмин Канагава', 'Гастрольный тур Мариинского театра, дирижёр В. Гергиев', 'Япония',
        'Иокогама', NULL, '3-13-24 Yamashitacho, Naka Ward, Yokohama, Kanagawa 231-0023, Япония',
        'В ноябре 2003 года Дмитрий Хворостовский приезжает в Японию. На сцене концертного зала Кенмин Канагава в Иокогаме артист в главной роли спектакля Мариинского театра «Евгений Онегин», дирижёр - Валерий Гергиев.',
        '09.11.2003');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100039, NULL, 'Татарский академический государственный театр оперы и балета им.М.Джалиля',
        'Шаляпинский оперный фестиваль', 'Россия', 'Казань ', NULL, 'Свободы пл., Казань, Респ. Татарстан, 420015',
        'В июне 1993 года в Казани проходит Всероссийский оперный фестиваль имени Фёдора Ивановича Шаляпина. Хворостовский приезжал сюда 4 года назад. Тогда к нему ещё не пришла мировая известность, он не перемещался со скоростью 3 страны в неделю, а сейчас он — звезда. Тогда Дмитирй оставил запись в Книге Почётных гостей фестиваля: "С благодарностью и невероятным удовольствием благодарю за долгожданную встречу. От современного певца Д. Хворостовского".',
        '06.06.1993');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100040, NULL, 'Сент-Дэвидс холл', 'IV Международный телевизионный конкурс вокалистов ВВС ', 'Великобритания',
        'Кардифф', NULL, 'Сент-Дэвидс холл, Кардифф Великобритания, Уэльс, Кардифф, The Hayes',
        'Дмитрий Хворостовский участвует в IV Международном телевизионном конкурсе вокалистов ВВС и наутро просыпается всемирно знаменитым. Деньги на фрак, в котором конкурсант был обязан выступать во II туре с симфоническим оркестром, Дмитрий занял у Ивари Илия, концертмейстера Ирины Архиповой, аккомпанировавшем ему в первом туре в Кардиффе, пообещав вернуть долг из призовых. Свое обещание Хворостовский выполнил: получил титул "Певец мира" и призовой конверт.  Там же, в Кардиффе, происходит судьбоносное знакомство с продюсером Марком Хилдрю, поступают десятки приглашений от ведущих театров Европы и Америки.',
        '11-17.06.1989');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100041, NULL, 'Амфитеатр «Фернанд Линдсей»', '«Le Festival de Lanaudière»', 'Канада', 'Квебек', NULL,
        'Квебек, Канада. 1655 Bd Base-de-Roc, Joliette, QC J6E 0L1',
        'В июле 1998 года Хворостовский впервые выступает на "Le Festival de Lanaudière" — самом крупном в Канаде фестивале классической музыки.В рамках Международного фестиваля в сопровождении Монреальского симфонического оркестра (дирижёр Шарль Дютуа) Хворостовский исполняет вокальный цикл М.П. Мусоргского «Песни и пляски смерти», арии из опер Дж. Верди «Бал-маскарад», «Трубадур», «Дон Карлос», каватина Фигаро из оперы Дж. Россини «Севильский цирюльник» и романс «Очи чёрные».',
        '18.07.1998');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100042, NULL, 'Театр Киль', 'Музыкальный фестиваль Шлезвиг-Гольштейна', 'Германия', 'Киль ', NULL,
        'Theater Kiel, Rathauspl. 4, 24103 Kiel, Германия',
        'Музыкальный фестиваль Шлезвиг-Гольштейна (МФШГ) — один из крупнейших фестивалей классической музыки в Европе. Каждый год организаторы МФШГ выдвигали на передний план отдельные страны. На фестивале выступали артисты из соответствующих стран. В Kiel Opera House Дмитрий Хворостовский выступает вместе с пианистом Ивари Илия.',
        '01.08.2008');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100043, NULL, 'Театр Синьорелли', 'Фестиваль "Тосканское солнце"', 'Италия', 'Кортон в Тоскане ', NULL,
        'Piazza Luca Signorelli, 16, 52044 Cortona AR, Италия',
        'В августе 2005 года Хворостовский приезжает в Кортону для участия в фестивале "Тосканское солнце". Это еще совсем молодое событие — в 2003 году его основали выпускница Московской консерватории виолончелистка Нина Котова и писательница Фрэнсис Мэйс, автор бестселлера "Под солнцем Тосканы". Хворостовский приедет в Кортону еще раз в 2006 году, выступая с китайским пианистом Лан Ланом.  Видео: https://www.youtube.com/watch?v=zMrlBhDKOIg&t=1s&ab_channel=MsSyara',
        '12.08.2005');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100044, NULL, 'Причал Кингс-Док', 'Концерт “Фанфары нового мира”', 'Великобритания', 'Ливерпуль', NULL,
        'Britannia Vaults, Royal Albert Dock, Liverpool L3 4AD, Великобритания',
        'Хворостовского приглашают в Ливерпуль для участия в концерте “Фанфары нового мира”. Событие приурочено к 500-летию открытия Америки и проходит в порту - это финальная точка масштабной праздничной регаты. В честь парусников, преодолевших океан, поют звезды мирового уровня, награды вручает король Испании. Хворостовский исполняет каватину Фигаро и “Очи черные”.',
        '1992');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100045, NULL, 'Галерея Рафаэля в музее Виктории и Альберта', 'Открытие фонда ArtPoint', 'Великобритания',
        'Лондон', NULL,
        'Галерея Рафаэля в музее Виктории и Альберта (Victoria & Albert Museum’s Raphael Gallery). Cromwell Road, London SW7 2RL',
        'В ноябре 2016 года Дмитрий Хворостовский вместе со своим давним партнером по сцене, концертмейстером Ивари Илией выступает на гала-вечере, посвященном открытию фонда ArtPoint.  Фонд, основанный в Великобритании, занимается поддержкой и развитием карьеры выдающихся художников. Дмитрий Хворостовский и его жена Флоранс — меценаты этого Фонда.',
        '13.11.2016');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100046, NULL, 'Вигмор-холл', 'Дебют в Лондоне', 'Великобритания', 'Лондон ', NULL,
        '(Wigmore Hall). 1st Floor, 32 Wigmore St, London W1U 2RP, Великобритания',
        'В камерном зале Вигмор-холла Хворостовскому довелось побывать не единожды, но всё же самым популярным его концертом остался дебют в 1989 году. Он случился сразу после судьбоносного конкурса в Кардиффе. Газета “Таймс” написала о выступлении так: "Пришел, спел, победил!". Несказанно повезло тогда и с аккомпаниатором: замечательный пианист Олег Бошнякович высоко оценил талант юного Дмитрия, назвав его «артистом невероятных, просто бездонных творческих возможностей».',
        '15.12.1989');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100047, NULL, 'Королевский театр Ковент-Гарден', 'Дебют с партией Риккардо в опере Беллини «Пуритане»',
        'Великобритания', 'Лондон ', NULL, 'Королевский театр Ковент-Гарден. Bow St, London WC2E 9DD, Великобритания',
        'Королевский театр Ковент-Гарден в Лондоне — «второй дом» Хворостовского: здесь в 1992 году он дебютирует с партией Риккардо в опере Винченцо Беллини «Пуритане», а через десять лет выступит в роли Графа ди Луна в премьере оперы «Трубадур» (Il Trovatore) в Лондонском Royal Opera House в постановке Элайджа Мошински совместно с Teatro Real Madrid. Постановка возобновлялась в Лондоне в 2004, 2007 и 2009 годах. Всего он выйдет на эту сцену 119 раз. Незадолго до своей смерти он репетирует главную партию в «Риголетто». Премьера постановки в декабре 2017 года будет посвящена памяти Хворостовского, а на сцену вместо него выйдет греческий баритон Димитрий Платаниас.',
        '12-29.05.1992');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100048, NULL, 'Королевский Альберт Холл ', 'Гала-концерт в честь празднования 80-летия Михаила Горбачева',
        'Великобритания', 'Лондон ', NULL, 'Imperial College Rd, South Kensington, London SW7 2AP, Великобритания',
        'Знаменитый Альберт-холл принимал Хворостовского 6 раз. По приятному совпадению, зал находится в элитном районе Кенсингтон - Дмитрий несколько лет снимал там квартиру. На этой же сцене давали концерты и Битлз, и Pink Floyd, и ABBA. Одним из самых примечательных его выступлений становится Gorby 80: неизменно харизматичный, он поет о любви на торжестве в честь юбилея Михаила Горбачева. В списке гостей Мила Йовович, Scorpions и другие.',
        '30.03.2011');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100049, NULL, 'Лондонский Колизеум', '«Беслан. Концерт ради будущего»', 'Великобритания', 'Лондон ', NULL,
        'Театр Колизеум (Coliseum). London Coliseum, St Martin''s Ln, London WC2N 4ES, Великобритания',
        '1 сентября 2004 года в городе Беслане в Северной Осетии террористы взяли в заложники больше 1000 человек и удерживали их в здании школы. Большинство удалось спасти, однако более 300 человек (детей и взрослых) погибли, а остальные получили ранения. В ноябре того же года Хворостовский участвует в благотворительном концерте «Беслан. Концерт ради будущего» в Колизеуме. Мероприятие посещает британский принц Чарльз. Часть средств будет перечислена жертвам теракта, а другая — вложена в культурное развитие на Кавказе.',
        '07.11.2004');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100050, NULL, 'Посольство РФ в Лондоне', 'Концерт для сотрудников посольства', 'Великобритания', 'Лондон ',
        NULL, '14 Victoria Grove Mews, London W2 4LN, Великобритания',
        'Хворостовский дает концерт для сотрудников российского посольства в Лондоне. Всего месяц назад ему поставили тревожный диагноз - опухоль мозга. Дмитрий поёт известнейшие произведения русских композиторов: Глинка, Чайковский, Рахманинов, Бородина. Дмитрий тогда сказал: "Я не хочу рассказывать вам о деталях и трудностях моего лечения, которое я прохожу, и буду проходить. Но я хотел собрать всех вас, потому что я очень по вам соскучился. И главная причина: так получилось, что я живу в Лондоне уже 20 с лишним лет, и я никогда здесь не бывал, а это просто нелепо. Так что я решил выступить здесь с концертом и очень рад тому, что вы все пришли. Спасибо вам!"  Это был акт единения с Россией, с русской культурой, с русской музыкой, которую он пел в российском посольстве.',
        '05.07.2015');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100051, NULL, 'Концертный зал Барбикан',
        'Выступление с Государственным академическим камерным оркестром России и Санкт-Петербургским коллективом народных инструментов «Стиль пяти». ',
        'Великобритания', 'Лондон ', NULL, '(Barbican Hall). Silk St, Barbican, London EC2Y 8DS, Великобритания',
        'Центр искусств Барбикан, в котором расположен Концертный зал, является ярким образцом стиля «брутализм», характерного для послевоенной Великобритании. В декабре 2003 года здесь выступает Хворостовский с Государственным академическим камерным оркестром России и Санкт-Петербургским коллективом народных инструментов «Стиль пяти». Певца дважды вызывают на бис — во второй раз он словно шепотом поет "Где-то далеко..." - «Песню о далёкой Родине» Микаэла Таривердиева.',
        '14.12.2003');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100052, NULL, 'Павильон Дороти Чендлер Лос-Анджелеской оперы',
        'Мировая премьера поэмы Георгия Свиридова «Отчалившая Русь» ', 'США', 'Лос-Анджелес', NULL,
        '135 N Grand Ave, Los Angeles, CA 90012, Соединенные Штаты',
        'Хворостовский поет “Отчалившую Русь” Свиридова в одном из крупнейших залов Лос-Анджелеса. Этот вокальный цикл на стихи Сергея Есенина уже хорошо знают в России - его исполняла Елена Образцова. Но зарубежный зритель впервые слышит “Русь” именно в исполнении Хворостовского. Придирчивый Свиридов отзывается о нём крайне лестно: "Я всю жизнь ждал своего певца и наконец дождался". Хворостовский исполнял поэмы Свиридова и на Западе, и в США, и везде музыка Свиридова в исполнении Дмитрия Хворостовского находила отклик в сердцах слушателей. Говоря о сложности исполнения музыки Свиридова, о том, что "это его крест", Хворостовский говорил и следующее: "Просто я хочу, чтобы великую русскую музыку, музыку Свиридова, узнал мир. Я могу это сделать и делаю, по мере сил и возможностей".',
        '01.03.1994');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100053, NULL, 'Столичный театр', 'Первый мастер-класс молодым финским певцам', 'Финляндия', 'Миккели', NULL,
        'Savilahdenkatu 11 50100 MIKKELI Puhelin 044 402 1539',
        'В июле 2002 года в небольшой город Миккели в Финляндии на родину Муми-троллей Дмитрий Хворостовский приезжает на следующий год после смерти Туве Янссон и за четыре года до триумфа финской группы Lordi на "Евровилении". Сейчас Хворостовский даёт свой первый мастер-класс молодым финским певцам. Всего Хворостовский успеет выступить в Финляндии более 15 раз и побывает в самых разных уголках страны.',
        '2002, июль');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100054, NULL, 'Театр Ла Скала', 'Дебют с партией Родриго в опере Дж. Верди "Дон Карлос"', 'Италия', 'Милан',
        NULL, 'Teatro alla Scala, Via Filodrammatici, 2, 20121 Milano MI, Италия',
        'В декабре 1992 года Хворостовский дебютирует с партией Родриго в знаменитой «большой» опере Верди на сцене театра Ла Скала. Именно этот театр возродил постановки первоначальной пятиактной французской версии 1866 года. Певцу едва исполнилось 30 лет. В декабре 2016 года по состоянию здоровья он вынужден будет отказаться от участия в двух спектаклях «Дон Карлос» в Большом театре.  Среди русскоязычных исполнителей Хворостовский окажется самым известным исполнителем партии Родриго.',
        '1992, декабрь');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100055, NULL, 'Дворец Республики', 'Концерт лауреатов конкурса имени Глинки в Минске', 'Белоруссия', 'Минск',
        NULL, 'Октябрьская площадь, 1, Минск, Белоруссия',
        'В октябре 1988 Хворостовскому исполняется 26 лет. И в том же месяце он участвует в концерте лауреатов конкурса имени Глинки в Минске на Октябрьской площади, дом 1. А уже 30 октября пройдет первый в современной истории Беларуси массовый митинг, организованный молодежным сообществом. Речь об Обществе молодых литераторов «Тутэйшыя» во главе с ровесником певца, Алесем Беляцким.',
        '14.10.1988');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100056, NULL, 'Зал камерной музыки Беларусской государственной филармонии', 'Дебютный сольный концерт в Минске',
        'Белоруссия', 'Минск', NULL, 'просп. Независимости, 50, Минск',
        'Там, где 2 месяца назад Хворостовский выступал как лауреат конкурса имени Глинки, теперь проходит его сольный концерт. Из книги воспоминаний пианиста Владимира Шелихина «Сердце помнит» упомянута встреча певца с президентом республики Станиславом Шушкевичем после концерта: «Когда Дмитрий спросил, ощущается ли в его речи акцент, глава государства шутливо сказал: "В разговоре — нет, а вот в мыслях слегка заметен"».',
        '1989');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100057, NULL, 'Монреальский симфонический дом', '200-летие со дня рождения А.С. Пушкина', 'Канада', 'Монреаль ',
        NULL, '1600 Rue Saint-Urbain, Montréal, QC H2X 0S1, Канада',
        'Летом 1999 года весь мир отмечает 200-летие Александра Сергеевича Пушкина (1999 год объявлен ЮНЕСКО годом А.С. Пушкина).  5 июня, накануне дня рождения поэта, Хворостовский дает концерт в La Maison Symphonique в Монреале. В 1997 году президент России Борис Ельцин своим указом установил Пушкинский день России и поручил отмечать его ежегодно 6 июня. В репертуаре певца немало романсов, написанных на пушкинские строки. Особенной популярностью среди поклонников пользуется романс М.И. Глинки «Я помню чудное мгновенье».',
        '05.05.1999');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100058, NULL, 'Колонный зал Дома Союзов', 'Первое выступление в Москве  ', 'Россия', 'Москва', NULL,
        'ул. Большая Дмитровка, 1/30, Москва',
        'В 1989 году Дмитрий Хворостовский одержал триумфальную победу на престижном вокальном конкурсе BBC в Кардиффе, после которой ему присваивается неофициальное звание «Лучший певец мира».  В том же году он впервые выступает в Москве в концерте-памяти Юрия Александровича Гуляева в Колонном зале Дома Союзов',
        '02.04.1989');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100059, NULL, 'Московская государственная консерватория им. П. И. Чайковского',
        'Первое выступление в Большом зале Московской консерватории', 'Россия', 'Москва', NULL,
        'Большая Никитская ул., 13/6, Москва, Россия',
        '25 декабря Хворостовский впервые выступает в Большом зале Московской консерватории. Он поет партию князя Елецкого в концертном исполнении оперы Чайковского «Пиковая дама», Знаменитая ария «Я вас люблю, люблю безмерно» входила в конкурсную программу, с которой Дмитрий выступал в Кардиффе на том самом конкурсе, после победы в котором он проснулся знаменитым.',
        '25.12.1989');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100060, NULL, 'Московский театр Новая Опера имени Е.В. Колобова', 'Премьера оперы «Риголетто»', 'Россия',
        'Москва', NULL, 'ул. Каретный Ряд, 3 строение 2, Москва, 127006',
        'Долгожданная премьера оперы «Риголетто» - совместного проекта Савоннлинского оперного фестиваля (Финляндия) и «Новой Оперы» (режиссёр - Ральф Лянгбака (Финляндия), дирижёр - Евгений Колобов (Москва)). Хворостовский дебютирует в партии Риголетто и удивляет всех, кто считал, что донжуанское амплуа — его пожизненный приговор. Оказалось, что он ничуть не менее убедителен в роли несчастного горбатого шута. Партия Риголетто станет одной из его коронных. После премьеры спектакль наградили множеством эпитетов: превосходный, шедевр, лучшая постановка «Новой Оперы», настоящий вердиевский спектакль!',
        '19/21.12.2000');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100061, NULL, 'Концертный зал им. П.И. Чайковского Московской государственной академической филармонии',
        'Партия Демона в опере Антона Рубинштейна', 'Россия', 'Москва', NULL, 'Тверская ул., д. 31/4, стр. 1 ',
        'Карьера Хворостовского наполнена дебютами. В 53 года он впервые поет партию Демона в одноименной опере Антона Рубинштейна в постановке Дмитрия Бертмана. Произведение, написанное 150 лет назад, так и не удостаивалось успешного сценического исполнения, пока за эту роль не взялся Дмитрий Хворостовский. В одном из интервью о работе над "Демоном" он говорит: "Вся моя жизнь - доказательство выхода за рамки, а вообще жизнь - она прекрасна, но, увы, коротка". Через три месяца у него обнаружат раковую опухоль.',
        '30.01-2/5.02.2015');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100062, NULL, 'Музыкальный театр "Геликон-опера"', 'Открытие исторической сцены театра', 'Россия', 'Москва',
        NULL, 'Большая Никитская ул., 19/16с1',
        'Хворостовский поёт на открытии исторической сцены театра “Геликон-опера”. С театром его уже связывают долгие отношения, он непосредственно участвовал в стройке театра.  Когда певец уйдет из жизни, в фойе “Геликон-опера” ему поставят бюст, а также откроют гримерную «Дмитрий Хворостовский». Открытие гримерки будет приуроченно к третьей годовщине премьеры спектакля «Демон».',
        '31.10.2015');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100063, NULL, 'Московский театр "Новая Опера" имени Е.В. Колобова',
        'Почетное звание «Народный артист Российской Федерации»', 'Россия', 'Москва', NULL,
        'ул. Каретный Ряд, 3 строение 2, Москва, 127006',
        '5 августа 1995 года за большие заслуги в области искусства Дмитрию Хворостовскому присваивают высшее почетное звание России в области искусства «Народный артист Российской Федерации». На тот момент Хворостовскому 35 лет. Он самый молодой среди тех, кого наградили этим званием в этом году. Почетный знак вручен Наиной Ельциной во время открытия сцены театра "Новая Опера".',
        '05.08.1995');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100064, NULL, 'Красная площадь', 'Первый сольный концерт на Красной Площади', 'Россия', 'Москва', NULL,
        'Красная площадь',
        'Популярность классической музыки в России благодаря Хворостовскому достигает невиданных высот — он становится первым оперным певцом, который дает бесплатный благотворительный сольный концерт с хором и оркестром на Красной площади. Мероприятие проводится в пользу ветеранов войны, при поддержке Администрации Президента России и при финансовом участии Сбербанка России. На концерте присутствует президент России Владимир Путин и президент Египта Хосни Мубарак.  Государственный академический камерный оркестр России (дирижёр К. Орбелян), при участии хора Йельского университета (США), камерного хора «Духовное возрождение» Московского государственного института музыки имени А.Г. Шнитке под управлением Льва Конторовича, Санкт-Петербургского коллектива народных инструментов «Стиль пяти». Прямая трансляция концерта ведется телеканалами "Россия" и "РТР – планета" в 37 стран мира на 41 миллион зрителей.',
        '28.05.2004');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100065, NULL, 'Московский театр "Новая Опера" имени Е.В. Колобова', 'Награждение Орденом Сергия Радонежского',
        'Россия', 'Москва', NULL, 'ул. Каретный Ряд, 3 строение 2, Москва, 127006',
        'В 51 год Дмитрий Хворостовский записывает сборник богослужебных песен, за что получает признание Русской православной церкви и награждается орденом Сергия Радонежского. Вручение проходит на сцене «Новой Оперы» в рамках программы «Звезды мировой оперы», Хворостовский выступает с оркестром театра «Новая Опера».',
        '16.07.2014');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100066, NULL,
        'Государственный академический музыкальный   театр им. К.С. Станиславского и В.И. Немировича-Данченко  ',
        'Оперный дебют на московской сцене', 'Россия', 'Москва', NULL, 'улица Большая Дмитровка, 17, Москва',
        'Оперный дебют Дмитрия Хворостовского на московской сцене в ведущей партии Онегина в опере Петра Ильича Чайковского «Евгений Онегин».',
        '26.11.1988');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100067, NULL, 'Театр Принца-Регента', 'Мюнхенский Оперный фестиваль', 'Германия', 'Мюнхен', NULL,
        'Prinzregentenpl. 12, 81675 München, Германия',
        'Традиционно летом Баварская государственная опера открывает свои двери для Мюнхенского оперного фестиваля. Его главная тема в 2013 году — 200-летие двух выдающихся композиторов: Рихарда Вагнера и Джузеппе Верди. Хворостовский и пианист Ивари Илия исполняют произведения Сергея Рахманинова и Ференца Листа, а под занавес удивляют требовательную публику исполнением произведений Николая Метнера.',
        '03.07.2013');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100068, NULL, 'Префектурный центр искусств Аичи', 'MET Japan Tour', 'Япония', 'Нагоя', NULL,
        '1 Chome-13-2 Higashisakura, Higashi Ward, Nagoya, Aichi 461-0005, Япония',
        'За два месяца до начала MET Japan Tour на АЭС Фукусима происходит авария, сравнимая с Чернобылем. Несколько участников тура отказываются ехать в Японию, опасаясь радиации. Хворостовский сообщает Метрополитен-опера, что готов ехать и выступать. 5 июня он находится в центре искусств префектуры Айти, исполняет партию Родриго в опере Дж. Верди "Дон Карлос". Японцы благодарят музыкантов за то, что они привезли надежду на скорое возрождение культурной жизни страны, пострадавшей от цунами.',
        '5/10.06.2011');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100069, NULL, 'Центр исполнительских искусств (Hyogo Performing Arts Center)', 'MET Japan Tour', 'Япония',
        'Нисиномия', NULL, '2-22 Takamatsucho, Nishinomiya, Hyogo 663-8204, Япония',
        'В 1995 году от сильного землетрясения пострадал японский город Нисиномия. После 10 лет восстановительных работ здесь открывается Центр исполнительских искусств в память о том самом землятресении. Площадка быстро становится одной из самых заметных в Японии и в мире. В июне 2006 года в Центре искусств проходят гастроли американского Metropolitan Opera с итальянской оперой Дж. Верди "Травиата" с русским баритоном Дмитрием Хворостовским в роли Жоржа Жермона.',
        '10/14.06.2006');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100070, NULL, 'Опера Ниццы (Opera de Nice)', 'Оперный дебют в Европе', 'Франция', 'Ницца', NULL,
        'Оперный театр Ниццы Франция, Прованс-Альпы-Лазурный Берег, Приморские Альпы, город Ницца, Rue Milton Robbins',
        'У Хворостовского оперный дебют в Европе с партией Елецкого в опере Чайковского "Пиковая дама" — только что он завоевал гран-при в XXXIV Международном конкурсе вокалистов в Тулузе, а теперь выступает в Ницце.',
        '11.11.1988');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100071, NULL, 'Терраса гостиницы «Негреско»', 'Первый контракт со студией Philips Classics ', 'Франция',
        'Ницца', NULL, '37 Prom. des Anglais, 06000 Nice, Франция Терраса гостиницы «Негреско»',
        'Дмитрий Хворостовский подписывает свой первый контракт с нидерландской звукозаписывающей компанией Philips Classics, на сольную запись. Это начало стремительной международной карьеры.  Philips Classics получает эксклюзивное право организовывать концерты певца и записывать аудио и видеодиски с его концертными программами. За 10 лет сотрудничества Philips выпустила 20 альбомов, включающих как сольные программы, так и арии из опер.',
        '1989, октябрь');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100072, NULL, 'Государственный концертный зал филармонии им. Арнольда Михайловича Каца ',
        'Международный рождественский фестиваль искусств', 'Россия', 'Новосибирск', NULL,
        'Красный пр-т., 18/1, Новосибирск',
        '18 декабря 2013 года Дмитрий Хворостовский принимает участие в концерте, который проходит на сцене Государственного концертного зала филармонии им. Арнольда Михайловича Каца в рамках Международного рождественского фестиваля искусств. Программа "Только для вас". Свое выступление Дмитрий посвящает Муслиму Магомаеву.    Сам Хворостовский упоминал, что однажды во время работы в Metropolitan Opera он сильно соскучился по русской музыке, зашел в интернет и за один раз прослушал около 40 композиций в исполнении Магомаева.',
        '18.12.2013');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100073, NULL, 'Театр Фриско', 'Дебют в партии Андрея Болконского', 'США', 'Даллас', NULL,
        '8004 Dallas Pkwy Suite 200, Frisco, TX 75034, Соединенные Штаты',
        'Дмитрий Хворостовский отправляется в Даллас, где дебютирует в партии Андрея Болконского в опере Сергея Прокофьева «Война и мир». Спектакль имеет успех, и показы продолжаются целый месяц.',
        '1991');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100074, NULL, 'Концертный зал "Карнеги-холл"', 'Хворостовский в Карнеги-холле', 'США', 'Нью-Йорк', NULL,
        '881 7th Ave, New York, NY 10019, Соединенные Штаты',
        'На сцене престижного Карнеги-холла проходили по большей части сольные концерты певца. Программа самая разнообразная: опера «Пиковая дама» и "Иоланта" Чайковского, опера «Разбойники» и "Бал-маскарад" Джузеппе Верди, опера «Фаворитка» Гаэтано Доницетти.  В Карнеги-холл исполнялись произведения композиторов: Габриэля Форе, Сергея Танеева, Ференца Листа, Петра Ильича Чайковского, Модеста Мусоргского, Георгия Свиридова, Мориса Равеля, Густава Малера, Анри Дюпарка, Михаила Глинки, Николая Римского-Корсакого, Рихарда Штрауса.   Карнеги стал значимой площадкой для творческой дружбы Хворостовского с Ивари Ильей: умный и деликатный аккомпанемент, яркий и выразительный вокал. Они познакомились еще в Кардиффе - играли в дуэте на том самом легендарном конкурсе, после которого Хворостовский проснулся знаменитым.  На сцену Карнеги-холла, как и на мировую сцену вообще, Хворостовских вывел многих российских артистов, известных сегодня: Анна Нетребко, Ильдар Абразаков, Екатерина Сюрина и другие. Он всегда помогал талантливым молодым артистам, как и его великий учитель Ирина Архипова.',
        '1991-2016');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100075, NULL, 'Отель «Плаза»', 'Благотворительный бал «Петрушка»', 'США', 'Нью-Йорк', NULL,
        '5th Avenue at, Central Park S, New York, NY 10019, США',
        '2 августа 2002 года Дмитрий Хворостовский принимает участие в ежегодном благотворительном бале «Петрушка». Бал организован с целью сбора средств и поддержки русской культуры в Америке и является главным благотворительным мероприятием «Общества Помощи Русским Детям», основанного семьями «белых» эмигрантов в 1926 г. Выступление проходит в бальном зале отеля «Плаза» при аншлаге.',
        '02.08.2002');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100076, NULL, 'Отель «Плаза»', 'Премия Opera News Awards', 'США', 'Нью-Йорк', NULL,
        'Grand Ballroom, The Plaza (Отель «Плаза»), New York. Fifth Avenue at Central Park South New York, NY 10019',
        '29 Апреля 2012 года на церемонии награждения музыкальной премии Opera News Awards Дмитрий Хворостовский получает почётную награду от критиков за достижения в области оперного искусства. Церемония проходит в большом бальном зале отеля Плаза в Нью-Йорке.',
        '29.04.2012');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100077, NULL, 'Метрополитен-опера', 'Дебют с оперой «Пиковая дама» (партия Елецкого)', 'США', 'Нью-Йорк', NULL,
        '50 Lincoln Center Plaza # 150, New York, NY 10023, Соединенные Штаты',
        'Метрополитен-опера занимает почетное первое место по частоте выступлений Хворостовского. На этой сцене он выступил около 180 раз. В 1995-м Дмитрий дебютировал в опере “Пиковая дама” Чайковского (партия Елецкого), в 2002-м в опере «Война и мир» Прокофьева (партия Болконского), в 2006-м в опере «Евгений Онегин» Чайковского (партия Онегина), в 2004-м и 2012-м в «Травиате» Дж. Верди (партия Жермона) , в 2013-м и 2015-м в опере «Трубадур» Дж.Верди (партия Графа ди Луна).',
        '1995, октябрь');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100078, NULL, 'Генеральное консульство Российской Федерации в Нью-Йорке', 'Концерт для сотрудников консульства',
        'США', 'Нью-Йорк ', NULL, '9 E 91st St, New York, NY 10128, Соединенные Штаты',
        '1999 год, Нью-Йорк. Любимый Дмитрием Метрополитен-опера оккупирован премией MTV Music Awards, Рики Мартин уносит все главные награды. Хворостовский же впервые выступает в российском консульстве, знакомит местный истеблишмент с русской культурой. В 2008 году здесь же он будет петь с американской оперной певицей Рене Флеминг. Хворостовский — живой проводник русской культуры. В интервью The New York Times он говорит, что в русской музыке есть всепроникающая задумчивость, «упрямство и дикость».    Интервью: https://www.nytimes.com/2008/03/30/arts/music/30tomm.html',
        '1999');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100079, NULL, 'Сцена Охридского античного театра', 'Открытие 53-го Летнего фестиваля "Охрид-2013"', 'Македония',
        'Охрид ', NULL, 'Охрид 6000, Северная Македония',
        'В июле 2013 года на открытии 53-го летнего фестиваля искусств в Охриде Хворостовский солирует в сопровождении Македонского хора и оркестра, дирижёр Константин Орбелян. Фестиваль в Охриде существует в 1961 года и ежегодно собирает величайших звёзд из мира музыки, поэтому участие в фестивале – огромная честь. В Охриде выступили такие музыканты, как Леонид Коган, Святослав Рихтер, Андре Наварра, Мстислав Ростропович, Руджеро Риччи, Сальваторе Аккардо, Елена Образцова, Катя Риччарелли, Виктория де лос Анхелес, Максим Венгеров, Мишель Камило, Пол Мейер, Лео Нуччи, Барбара Фриттоли, Джесси Норман, Найджел Кеннеди, Зубин Мехта, Эннио Морриконе.',
        '12.07.2013');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100080, NULL, 'Театр Шатле', 'Опера «Евегний Онегин» (партия Онегина) ', 'Франция', 'Париж', NULL,
        '2 Rue Edouard Colonne, 75001 Paris, Франция',
        'Первое выступлений Хворостовского в парижском театре Шатле. Он поет партию Евгения Онегина в одноименной опере в постановке Адольфа Дрезена. Отношения с режиссером не заладились, кардинально отличаются взгляды на сюжет. Хворостовский заявляет: "Постановка совсем "не про то!" Его возмущают "и безобразные платья, и роговые очки Лариной, и дамы в шляпах с сетками от пчел". Хворостовский всегда сопротивлялся особенно сильным перегибам режиссёров. Но  премьера всё же состоится.  В 1993 вышла цифровая запись коллекции образцов оперных спектаклей в серии "Оперные премьеры", выпуск 1993 г., куда вошла сцена "В вашем доме!" из оперы "Евгений Онегин" в исполнении: Дмитрия Хворостовского (партия Онегин), Сары Уолкер (партия Ларина), Нуччи Фочиле (партия Татьяна), Ольги Бородиной (партия Ольга), Нила Шикоффа  (партия Ленский) с Санкт-Петербургским камерным хором и оркестром Парижа.',
        '04.10.1992');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100081, NULL, 'Пермский государственный академический театр оперы и балета им. П. И. Чайковского',
        'Всероссийский конкурс вокалистов оперно-камерного пения', 'Россия', 'Пермь', NULL,
        'Сибирская ул., 11А, Пермь, Россия',
        'Начиная с третьего курса института Дмитрий Хворостовский начинает работать в Красноярском государственном театре оперы и балета. За пять лет он исполнил почти все ведущие баритоновые партии. Следующая цель — вокальные конкурсы. В июне 1987 года Хворостовский побеждает на первом в своей жизни Всероссийском конкурсе вокалистов в Перми: становится Лауреатом I премии и награждается дипломом "за лучшее исполнение романсов С.И.Танеева". Вокальные миниатюры Сергея Танеева являются наивысшим достижением вокальной лирики начала ХХ века и технически очень сложны для вокалистов.',
        '20-29.06.1987');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100082, NULL, 'Концертный зал Хасколабио', 'Концерт в рамках АРТ фестиваля', 'Исландия', 'Рейкьявик', NULL,
        '42RV+3XX, Vesturbær University of Iceland, Reykjavík, Исландия',
        'Продажи билетов на майское выступление Хворостовского в Концертном зале Хасколабио в Рейкьявике в рамках АРТ фестиваля стартуют за полгода, «чуть раньше обычного», отмечают организаторы. В фестивале участвует и Брин Терфель. В 1989 году в Кардифе Дмитрий обошел Брина в конкурсе BBC «Певец мира». Оба певца опираются на «родной» материал: Хворостовский - на Чайковского и Мусоргского; Терфель — на шотландские, ирландские и уэльские песни. Дмитрий выступает 20 мая 2007 года, Терфель - 21 мая."',
        '20.05.2007');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100103, NULL, 'Гранд Опера', 'Премьера оперы «Риголетто»', 'США', 'Хьюстон', NULL,
        '510 Preston St UNIT 1504, Houston, TX 77002, Соединенные Штаты',
        'В октябре-ноябре 2001 года в техасском городе Хьюстон Хворостовский восемь раз выходит на сцену Houston Grand Opera с партией Риголетто в одноименной опере Дж. Верди. Через 5 лет, осенью 2006 года Дмитрий дебютирует в Houston Grand Oper с партией Симона Бокканегры из одноимённой оперы Дж. Верди, дирижёр - Патрик Саммерс',
        '2001');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100083, NULL, 'Театр Гион', 'Гастроли в Италии', 'Италия', 'Рим', NULL,
        'Via delle Fornaci, 00165 Roma RM, Италия',
        'Октябрь 1997 года Хворостовский прибывает в город Рим, чтобы выступить на сцене Театра Гионе. Театр был назван в честь Франко Гионе (1886–1964) — знаменитого дирижера и скрипача, работавшего над оперой Верди «Травиата» с Марией Каллас. На репетиции перед концертом Хворостовский шутит, что сцена «косая». Пианист Михаил Аркадьев соглашается и добавляет «Вот такой театрик».    Видео репетиции: https://www.youtube.com/watch?v=kJk_S7a0j2A&t=5s&ab_channel=MikhailArkadev%C2%ABPianoMasterKey%C2%BB',
        '15.10.1997');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100084, NULL, 'Крепость "Олафсборг"', 'Савонлиннский оперный фестиваль', 'Финляндия', 'Савонлинна', NULL,
        'Крепость Святого Олафа. 57130 Savonlinna, Финляндия',
        'В старинной крепости Олафсборг среди озёр Финляндии проходит Савонлинский оперный фестиваль. Дмитрий Хворостовский впервые сюда приезжает в июле 1998 года. В рамках Савонлиннского оперного фестиваля Дмитрий исполняет партию Франческо в опере Джузеппе Верди "Разбойники" театра Ковент-гарден.  В последующем, в июле 2001 и 2002 года Хворостовский снова посетит Финляндию -состоится Гастрольный тур театра "Новая Опера" (Москва). Дмитрий выступит с партией "Риголетто" в одноименной опере Дж. Верди (дирижёр Евгений Колобов, 2001 г., дирижёр Альберто Холд-Гарридо (Alberto Hold-Garrido), 2002 г. ).',
        '1998, 2001, 2002');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100085, NULL, 'Академический театр оперы и балета имени С. М. Кирова', 'Дебют в Мариинском театре', 'Россия',
        'Санкт-Петербург', NULL, 'Театральная площадь, 1, Санкт-Петербург, Россия',
        'До 1990 года Дмитрий Хворостовский работает в Красноярском государственном театре оперы и балета.  В 1988 году певец впервые принял участие в оперной постановке за пределами Красноярского театра оперы и балета в ведущей партии Валентина оперы Шарля Гуно "Фауст" в Кировском театре.',
        '06.11.1988');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100086, NULL, 'БЗК «Октябрьский»', 'Проект "Хворостовский и друзья"', 'Россия', 'Санкт-Петербург', NULL,
        'Лиговский пр., 6, Санкт-Петербург, 191036',
        'В 2006 году Дмитрий Хворостовский создаёт проект "Хворостовский и друзья". Местом для проведения его выступлений становится Большой концертный зал "Октябрьский". Основная задача певца - познакомить российского слушателя с лучшими оперными исполнителями со всего мира. В 2013 году в честь своего 50-летия Дмитрий собрал на одной сцене целую плеяду артистов мировой оперы.',
        '2006');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100087, NULL, 'Ледовый дворец', 'Концерт в честь 50-летия певца', 'Россия', 'Санкт-Петербург', NULL,
        'пр. Пятилеток, 1, лит. А, Санкт-Петербург, 193318',
        'Хворостовский празднует 50-летие юбилейными концертами в Санкт-Петербурге и Москве. Правда, есть нюанс: день рождения у певца случился еще 16 октября. Однако из-за плотного графика он встретил юбилей на репетициях в Нью-Йорке, а на родине оказался только 3 месяца спустя. 21 января 2013 состоялся концерт из цикла "Хворостовский и друзья" в Ледовом дворце в Санкт-Петербурге. Благодаря проекту "Хворостовский и друзья" в России выступили многие звезды мировой оперы. В концерте принимают участие Барбара Фриттоли, Рамон Варгас, Екатерина Губанова, Аскар и Ильдар Абдразаковы, группа «Кватро», Петербургский камерный хор, Балтийский симфонический оркестр, дирижёр К. Орбелян.',
        '23.01.2013');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100088, NULL, 'Петропавловский собор', 'Альбом песнопений «Credo»', 'Россия', 'Санкт-Петербург', NULL,
        'Петропавловская крепость, Санкт-Петербург, 197046',
        'Съёмка клипа «Утверди, Боже!» (муз. А. Косолапова) для продвижения диска «Credo» литургической православной музыки, духовных песнопений на канонический церковный текст, который был записан в августе 1994 с Санкт-Петербургским камерным хором (художественный руководитель и главный дирижёр Николай Корнев). Альбом выпускается звукозаписывающей компанией Philips Classics Productions в октябре 1996 г., в России диск выходит под названием «Верую». В мае 1996 г. Хворостовский с хором Николая Корнева в Санкт-Петербургском зале Государственной академической капеллы им. М.И. Глинки запишет диск русских народных песен «Kalinka» (Philips Classics, октябрь 1998)',
        '1994, август');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100102, NULL, 'Башкирский государственный театр оперы и балета',
        'Благотворительный концерт "Дмитрий Хворостовский и друзья - детям"', 'Россия', 'Уфа', NULL,
        'ул. Ленина, 5/1, Уфа',
        'Хворостовский приезжает в Уфу для участия в благотворительном концерте "Дмитрий Хворостовский и друзья - детям". В концерте принимает участие оперный певец Ильдар Абдразаков, коренной уфимец, Национальный симфонический оркестр Республики Башкортостан, дирижёр Константин Орбелян. Концерт собирает для тяжелобольных детей рекордные 37,5 миллионов. Сумма феноменальная: в 2 раза больше, чем годом ранее в Кремлевском дворце.',
        '18.11.2015');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100089, NULL, 'Государственный комплекс «Дворец конгрессов»', 'Приз «Муза фестиваля Дворцы Санкт-Петербурга»',
        'Россия', 'Санкт-Петербург', NULL, 'Санкт-Петербург, Стрельна, ул. Глинки, д. 1.',
        '43-летний Хворостовский участвует в масштабном концерт в Константиновском дворце — в рамках благотворительного турне, посвященного 60-летию Победы. Участвуют 250 музыкантов и артистов: Государственный академический камерный оркестр России (за пультом Константин Орбелян), Санкт-Петербургский коллектив народных инструментов «Стиль Пяти», хор Академии хорового искусства России под управлением Виктора Попова и Всемирный детский хор ЮНЕСКО.',
        '13.06.2005');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100090, NULL, 'Шереметьевский дворец-Музей музыки', 'Выставка памяти Дмитрия Хворостовского', 'Россия',
        'Санкт-Петербург ', NULL, 'Набережная реки Фонтанки, 34, Санкт-Петербург',
        '4 сентября 2020-го в петербургском Музее музыки открывается выставка памяти Хворостовского. Экспонаты предоставляет семья певца и Красноярский краеведческий музей. Экспозиция получается изумительно подробная: от младенческих распашонок и школьных дневников до президентских орденов и призов Metropolitan Opera Club. Архивные фото, сценические костюмы, афиши, диски.',
        '2020');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100091, NULL, 'Дворцовая площадь', 'Оперный вечер в честь дня рождения Петербурга', 'Россия',
        'Санкт-Петербург ', NULL, 'Дворцовая пл., Санкт-Петербург, 191186',
        'Хворостовский участвует в гала-концерте "Классика на Дворцовой" в рамках празднования Дня города Санкт-Петербурга. Представление разворачивается под открытым небом, на фоне Эрмитажа. За костюмированным шоу и фантазийными видеопроекциями наблюдает около полумиллиона зрителей. Дмитрий исполняет неаполитанскую песню Родольфо Фальво, "Очи чёрные".',
        '27.05.2017');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100092, NULL, 'Музей Фаберже', 'Концерт для гостей Петербургского экономического форума', 'Россия',
        'Санкт-Петербург ', NULL, 'набережная реки Фонтанки, 21, Санкт-Петербург, 191023',
        'В июне 2016 года Хворостовский дает концерт для гостей Петербургского экономического форума в Белоколонном зале музея Фаберже. Для Дмитрия это не первое появление на ПМЭФ — четырьмя годами ранее он исполнял классические и современные мелодии на Дворцовой площади.',
        '16.06.2016');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100093, NULL, 'Культурный центр Седжонг', 'Концерт с Суми Йо', 'Корея', 'Сеул ', NULL,
        '175 Sejong-daero, Jongno-gu, Seoul, Республика Корея',
        'Хворостовский дает концерт "Хворостовский и друзья" в Сеуле, в ансамбле с Суми Йо. На родине певицы дуэт выступает уже второй раз. Суми является самой знаменитой оперной певицей из Юго-Восточной Азии, обладающей голосом колоратурное сопрано. Хворостовский настойчиво рекомендует ей обогатить свой репертуар русской музыкой и приглашает в Россию. Позже она прислушается к совету. В 2014 году певица приедет в Россию и даже побывает на малой родине Хворостовского – в Красноярске. Там Суми Йо исполнит в том числе романсы Рахманинова: "Я всегда хотела посетить вашу страну, потому что Хворостовский с большой теплотой рассказывал мне о России".',
        '28/30.05.2009');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100094, NULL, 'Концертный зал «New Wave Hall»',
        'Выступление на международном конкурсе молодых исполнителей “Новая волна-2016”', 'Россия', 'Сочи', NULL,
        'Олимпийский просп., 17, Сочи, Краснодарский край, 354340',
        'Последнее выступление Хворостовского на международном конкурсе молодых исполнителей “Новая волна-2016” в Сочи. 7 сентября 2016 года в рамках конкурса состоялся творческий вечер — композитора и продюсера Игоря Крутого. В этот день на сцену вышли звезды мировой величины – Дмитрий Хворостовский, Лара Фабиан, Анна Нетребко, которым аккомпонимировал на рояле сам маэстро. На концерте Хворостовский спел несколько композиций на музыку Игоря Крутого в сопровождении Президентского оркестра Республики Беларусь, дирижёр Виталий Кульбаков',
        '07.09.2016');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100101, NULL, 'Тюменская государственная филармония',
        'Концерт в рамках проекта "Дмитрий Хворостовский и друзья"', 'Россия', 'Тюмень', NULL,
        'ул. Республики, 34, Тюмень, Тюменская обл., 625000',
        'В 2006 году, чтобы познакомить россиийских слушателей со звездами мировой величины, Дмитрий Хворостовский создает свой авторский проект «Хворостовский и друзья». В разное время в проекте участвовали американская оперная певица Рене Флеминг, корейская дива Суми Йо, ведущая солистка театра «Метрополитен Опера» Сандра Радвановски, известный немецкий тенор Йонас Кауфман и многие другие. В 2010 году вместе с Чарльзом Кастроново и Екатериной Сюриной Хворостовский проводит серию выступлений в нескольких городах России. В Тюмени в программе концерта звучат арии и ансамбли из опер Беллини, Верди, Россини, Бизе и Чайковского.',
        '17.12.2010');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100095, NULL, 'Церковь Святого Павла',
        'Концерт с Московским симфоническим оркестром, дирижёр Константин Орбелян', 'Франция', 'Страсбург ', NULL,
        '1 Pl. du Général Eisenhower, 67000 Strasbourg, Франция',
        'В июле 2002 года Хворостовский выступает в Церкви Святого Павла в Страсбурге. Неоготический храм служит концертной площадкой для Государственного академического камерного оркестра России под управлением номинанта на премию Грэмми Константина Орбеляна, при участии Санкт-Петербургского коллектива народных инструментов «Стиль Пяти».    Совместно они запишут несколько альбомов, в которые войдут русские романсы и песни военных лет: «I Met You, My Love – Old Russian Romances» (Delos Records) – «Я встретил вас – старинные русские романсы»; «Where Are You My Brothers?» (Delos Productions) – «Где же вы теперь, друзья-однополчане?» (песни военных лет); «Moscow Nights» (Delos Productions) – «Подмосковные вечера» (песни советских композиторов); «Wait for Me» (Delos) – «Жди меня» и другие.',
        '21.07.2002');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100096, NULL, 'Большой концертный зал Тбилисской филармонии', 'Благотворительный концерт в Тбилиси', 'Грузия',
        'Тбилиси', NULL, '1, 0179 Petre Melikishvili St, Tbilisi, Грузия',
        'Хворостовский выступает в благотворительном концерте в Тбилиси в сопровождении симфонического оркестра Тбилисского театра оперы и балета им. Захария Палиашвили, дирижёр Заза Азмайпарашвили. В концерте приняли участие грузинские оперные звезды - Паата Бурчуладзе (основатель благотворительного фонда «Иавнана» для оказания помощи детям, лишенным родительской опеки, многодетным семьям) и грузинская оперное сопрано Тамар Ивери. Организует событие благотворительный фонд “Колыбель”, который оказывает помощь многодетным семьям.  Хворостовский перенимает опыт грузинских коллег и спустя несколько лет создает свой благотворительный проект «Дмитрий Хворостовский и друзья - детям»',
        '04.12.2010');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100097, NULL, 'Концертный зал телерадиокомпании Эн-Эйч-Кей',
        'Концерт с симфоническим оркестром NHK, дирижёр Ричард Брэдшоу', 'Япония', 'Токио', NULL,
        '5-45 Uenokoen, Taito City, Tokyo 110-8716, Япония',
        '2000 год, Дмитрию Хворостовскому 38 лет. Только что ему присвоили звание Почетного гражданина Красноярска, за личные выдающиеся заслуги в развитии культуры и искусства города. Он закончил работу над фильмом-оперой "Don Giovanni: Leporello’s Revenge" и приехал в Токио выступить на местном телевидении по приглашению одного из старейших японских оркестров NHK. За пультом — британский и канадский дирижёр Ричард Брэдшоу.',
        '15.10.2000');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100098, NULL, 'Центр искусств «Четыре сезона»', 'Trio Magnifico', 'Канада', 'Торонто ', NULL,
        '145 Queen St W, Toronto, ON M5H 4G1, Канада',
        '25 апреля 2017 года Дмитрий принимает участие в гала-концерте, который проходит в Торонто. В этот день вместе с ним на сцену выходят оперная певица Анна Нетребко и Юсиф Эйвазов, азербайджанский оперный певец. Певцы впервые участвуют в концерте вместе. Именно поэтому концерт называется "Trio Magnifico" ("Великолепное трио").',
        '25.04.2017');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100099, NULL, 'Площадь возле Нидаросского собора.', 'Праздник Святого Олафа', 'Норвегия', 'Тронхейм', NULL,
        'Kongsgårdsgata 2, 7013 Trondheim, Норвегия',
        'Каждое лето жители и гости Тронхейма переодеваются в средневековую одежду и живут в стиле XI века, отмечая праздник в честь легендарного правителя Норвегии Святого Олафа. 29 июля 2008 года — в день почитания Святого Олафа — Дмитрий Хворостовский и Ивари Илья выступают на площади возле Нидаросского собора — места коронации норвежских монархов. Со стороны это выглядит как небольшое путешествие в прошлое с рыцарями на конях,только рыцарей можно снимать на мобильные телефоны.',
        '29.07.2008');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100100, NULL, 'Оперный театр в Капитолии', 'XXXIV Международный конкурс вокалистов', 'Франция', 'Тулуза', NULL,
        'Франция, Окситания, Верхняя Гаронна, Тулуза, Rue du Poids de l''Huile',
        'Это первая поездка Хворостовского в Европу. Он приезжает во Францию, где в это время идут выборы президента. В Тулузе певец завоевывает Гран-при на XXXIV Международном конкурсе вокалистов. Однако не обходится без казуса: за день до выступления выясняется, что текст каватины Валентина из «Фауста», которую должен был исполнять Дмитрий на французском языке, не является оригинальным! Приходится срочно менять программу: Дмитрию разрешили заменить каватину на арию Елецкого из оперы Чайковского "Пиковая дама".  В восторге от выступления Хворостовского было жюри и его председатель Рольф Либерман, который так заслушался, что забыл позвонить в колокольчик и объявить следующего конкурсанта.  Спустя месяц в голосовании победит Франсуа Миттеран: со вторым сроком его президентство станет самым долгим в истории республики.',
        'октябрь 1988');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100104, NULL, 'Цюрихский оперный театр', 'Сольный концерт совместно с Ивари Ильей', 'Швейцария', 'Цюрих', NULL,
        'Sechseläutenpl. 1, 8008 Zürich, Швейцария',
        '27 февраля 2014 года Дмитрий Хворостовский дает сольный концерт в Цюрихском оперном театре. В программу вошли произведения российских и западноевропейских композиторов.  До этого певец был в этой стране летом 2004 года с выступлеием в опере Чайковского "Евгений Онегин" (партия Онегина).  Следующим городом, где пройдут его выступления станет Брюссель (Бельгия).',
        '27.02.2014');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100105, NULL, 'Театр Лирик Опера', 'Дебют в "Травиате"', 'США', 'Чикаго', NULL,
        '20 N Upper Wacker Dr, Chicago, IL 60606, США',
        'С Лирик-оперой Хворостовского связывают долгие теплые отношения. В 1993 году на сцене Лирик Опера в Чикаго состоялся американский дебют Хворостовского в "Травиате" Дж. Верди, ещё по 6 выступлений с партией папы Жермона было сыграно здесь в 1998 и 1999 гг. Между этими годами, в 1996 было участие в опере Шарля Гуно "Фауст" (партия Валентина), а в 2003 дебют с партией Ренато в опере "Бал-маскарад" Дж. Верди. В 2008 Хворостовский участвует в опере "Евегний Онегин" Чайковского (партия Евгений Онегин). В августе 2011 г. ушёл в отставку генеральный директор Лирик Оперы, занимавший эту должность с 1997 г. Концерт Дмитрия Хворостовского и Рене Флеминг, посвящённый Уильяму Мэйсону, состоялся 07 января 2012 г. Последнее выступление Хворостовского на этой сцене прошло 26 февраля 2016.',
        '1993-2016');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100106, NULL, 'Концертный зал Ашер-Холл',
        'Сбор средств для шотландского фонда «Национальные коллекции произведений искусства»', 'Великобритания',
        'Эдинбург', NULL, '(Usher Hall). Lothian Rd, Edinburgh EH1 2EA, Великобритания',
        'С самого начала своей международной карьеры Хворостовский занимается благотворительностью. В начале 1990 года, пока в Москве выстраиваются очереди в первый Макдоналдс, певец отправляется в свои первые зарубежные гастроли. В феврале он даёт концерт в эдинбургском Usher Hall в рамках мероприятий по сбору средств для шотландского фонда «Национальные коллекции произведений искусства». В программе арии из опер Чайковского, Леонкавалло, Верди. На бис — ария Альфонсо из «Фаворитки» Гаэтано Доницетти.',
        '12.02.1990');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100107, NULL, 'Российский государственный академический театр драмы им. Фёдора Волкова',
        'Роль Земнухова в опере "Молодая гвардия"', 'Россия', 'Ярославль', NULL,
        'пл. Волкова, 1, Ярославль, Ярославская обл., 150000',
        'Гастроли Красноярского театра оперы и балета в Ярославле. Хворостовскому всего 22 года, и сообразно возрасту он играет роль Вани Земнухова в опере Юлия Мейтуса «Молодая гвардия». На премьеру приезжает Валерия Борц, одна из немногих выживших молодогвардеек. Её поражает не только глубина исполнения Хворостовского, но и его сходство с реальным Земнуховым.',
        '13.07.1985');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100108, NULL, 'Дэвид-Геффен Холл', 'Концерт с Лучано Паваротти', 'США', 'Нью-Йорк', NULL,
        'Richard Tucker Music Foundation, Suite 11A, 277 West End Avenue, New York, N.Y. 10023.',
        '11 ноября 1990 года, Нью-Йорк, Хворостовскому 28 лет, он стоит на одной сцене с Лучано Паваротти. Они поют дуэтом арию ''''Invano Alvaro'''' из «Силы судьбы» Джузеппе Верди. Концерт организован Фондом имени Ричарда Такера, легендарного американского тенора. Среди участников — новые и лучшие голоса со всего мира. Много лет спустя Хворостовский будет вспоминать эти несколько часов как одно из важнейших событий в своей жизни.    Видео: https://www.youtube.com/watch?v=yvlqjyWw1Yk',
        '11.11.1990');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100109, NULL, 'Театр Колон', 'Серия спектаклей "Евгений Онегин"', 'Аргентина', 'Буэнос-Айрес', NULL,
        'Cerrito 628, C1010 CABA, Аргентина',
        '16 октября — свой 35 день рождения – встретил на гастролях в Австралии. В тот год его сольные камерные концерты прошли также в Барселоне, Мадриде, Риме, Тулузе, Нью-Йорке, Сан-Франциско, Рио-де-Жанейро, Мехико, Сан-Паулу. Дмитрий Хворостовский выступил в театре Колон в Аргентине с серией спектаклей «Евгений Онегин».',
        'декабрь 1997');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100110, NULL, 'Площадь парламента Армении', 'Выступление под открытым небом', 'Армения', 'Ереван', NULL,
        '19 Marshal Baghramyan Ave, Yerevan 0095, Армения',
        'В 2006 году профессору Красноярской государственной академии музыки и театра Екатерине Константиновне Иофель, преподавателю Дмитрия Хворостовского, была присуждена золотая медаль фонда Ирины Архиповой «За выдающийся педагогический вклад в дело воспитания нескольких поколений талантливых певцов». В этом же году Дмитрий – уже всемирно известный оперный певец – выступит акапелльно под открытым небом напротив парламента в Ереване.',
        '2006');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100111, NULL, 'Королевский оперный театр Маската', 'Дебют в Омане', 'Оман', 'Маскат ', NULL,
        'Royal Opera House Al Kharjiyah Street مسقط OM، 103, Оман',
        'В 2001 году правитель Омана Кабус бен Саид распорядился о строительстве оперного театра. Султан был большим поклонником классической музыки. Открытие театра состоялось в 2011 году с оперы "Турандот" в исполнении испанского тенора Пласидо Доминго. А в 2016 году в театре состоялся концерт Дмитрия Хворостовского с оркестром «Карло Феличе» из Генуи под руководством дирижёра Константина Орбеляна.',
        '19.01.2016');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100112, NULL, 'Концертный зал имени Ватрослава Лисинского', '«Lisinski Arioso»', 'Хорватия', 'Загреб', NULL,
        'Trg Stjepana Radića 4, 10000, Zagreb, Хорватия',
        'Концерт Дмитрия Хворостовского в рамках цикла «Lisinski Arioso» с Асмик Григоря и Каунасским симфоническим оркестром. Концерт состоялся в Концертном зале имени Ватрослава Лисинского – хорватского режиссёра, «основоположника новой хорватской и южнославянской музыки», как написано на его надгробном памятнике.',
        '13.05.2016');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100113, NULL, 'Муниципальный театр', 'Концерт с Ольгой Бородиной', 'Бразилия', 'Рио-де-Жанейро', NULL,
        'Praça Floriano, S/N - Centro, Rio de Janeiro - RJ, 20031-050',
        'В 1997 году Дмитрий Хворостовский гастролирует в Южной Америке. Он дает концерты вместе с Ольгой Бородиной – оперной звездой мирового масштаба, с которой Дмитрий знаком еще с начала музыкальной карьеры – вместе певцы участвовали в XII Всесоюзном конкурсе исполнителей им. Глинки.',
        '08.09.1997');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100114, NULL, 'Центр оперного пения Галины Вишневской', ' «Вечер русского романса»', 'Россия', 'Москва', NULL,
        'ул. Остоженка, 25, строение 1, Москва, 119034',
        'В октябре 2002 года в Москве произошел теракт: около 40 вооруженных человек захватили в заложники зрителей мюзикла "Норд-Ост". В ходе теракта на Дубровке 174 человека. Освободить заложников удалось только через три дня – 26 октября. 27 октября Дмитрий Хворостовский исполнил русские романсы в Центре оперного пения Галины Вишневской в память о жертвах теракта.',
        '27.10.2002');
INSERT INTO public.spectacles (id, static_id, name, subject, country, city, place, address, description, year)
VALUES (100115, NULL, 'Амфитеатр Раананы (Ra''anana Amphipark)', 'Концерт с Раананским симфоническим оркестром',
        'Израиль', 'Тель-Авив', NULL, 'Sderot Sha''ul HaMelech, Tel Aviv-Yafo, Израиль',
        'В 1921 году в Израиле было основано маленькое поселение. Вначале поселение называлось «Ахуза А - Нью-Йорк». Арабы этого региона называли его «Маленькой Америкой», так как большинство его жителей говорили по-английски и были выходцами из Нью-Йорка. Позже поселенцы-основатели выбрали его официальным названием «Раанана». Именно в этом небольшом городке возле Тель-Авива Дмитрий Хворостовский выступил с местным сифоническим оркестром. Дирижировал Ион Марин.',
        '04.07.2009');


